#!/usr/bin/env python

long_number = ''''''

# The 'solution' variable should hold the
# solution when the script is done.
solution = 0

# Your code goes here.
long_string = str(long_number)
a = 0
multiply = []
consec_12 = []
while a < 989:
    consec_12.append(long_string[a:12+a])
    a += 1

for i in consec_12:
    first = 1
    digits = []
    for ch in i:
        digits.append(ch)

    for j in digits:
        b = first * int(j)
        first = b
    multiply.append(b)

solution = (max(multiply))

# Check for the correct answer.
print("#4 : Consecutive Product ::", solution, "Correct." if solution == 2257403904 else ("Wrong: " + str(solution)))
